// src/pages/HomePage.js
import React, { useState } from 'react';
import Fuse from 'fuse.js';
import SearchBar from '../components/SearchBar';
import ResultsContainer from '../components/ResultsContainer';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [results, setResults] = useState([]);

  // Mock data for searching NEEDS to be updated with ( const handleSearch = async (query) => {) ) to query the database.
  const mockData = [
    { title: 'Introduction to React', description: 'Learn the basics of React.' },
    { title: 'Advanced React', description: 'Dive deeper into React hooks and state management.' },
    { title: 'React Router Guide', description: 'A complete guide to using React Router.' },
    { title: 'Managing State with Redux', description: 'Understand how to manage state in React with Redux.' },
    // Add more mock data here as needed
  ];

  // Search function using Fuse.js for advanced matching
  const handleSearch = (query) => {
    const options = {
      keys: ['title', 'description'], // Fields to search in
      threshold: 0.3, // Adjust this for more or less strict matching
    };

    const fuse = new Fuse(mockData, options); // Initialize Fuse with the data and options
    const result = fuse.search(query); // Perform the search

    // Extract and map the results from Fuse.js format
    const processedResults = result.map(({ item }) => item);

    setResults(processedResults); // Update the results state
  };

  return (
    <div className="home-page">
      <header className="header">
        <h1>GEN-I-E</h1>
        <SearchBar onSearch={handleSearch} />
      </header>
      <ResultsContainer results={results} />
      <div className="buttons">
        <Link to="/create-knowledge-holder" className="btn btn-primary">
          Create Knowledge Holder
        </Link>
        <Link to="/view-knowledge-holders" className="btn btn-secondary">
          View Knowledge Holders
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
